'use strict';

var Q = require ('q');
var fs = require('fs');

var Version = function() {

  var thePromise;

  var deferred = Q.defer();
  fs.readFile('VERSION', 'utf-8', function (error, text) {
    if (error) {
      console.log('failed');
      deferred.reject(new Error(error));
    } else {
      console.log('succeeded');
      deferred.resolve(text);
    }
  });
  thePromise = deferred.promise;
  thePromise.then(function() {
    }//,
    //function(reason) {
    //}
  );
  thePromise.done();

  //return {
  //  toString : function(){ return complete; }
  //};

};

module.exports = Version;
